globals.formatTime = function formatTime(seconds) {
  // Celé minuty
  const minutes = Math.floor(seconds / 60);

  // Sekundy s desetinnými místy
  const sec = (seconds % 60).toFixed(2);

  // Vrácení ?asu ve formátu mm:ss.ss
  return `${String(minutes).padStart(2, "0")}:${String(sec).padStart(5, "0")}`;
}

